func main() {
	reader := bufio.NewReader(os.Stdin)
	s, _ := reader.ReadString('\n')
	s = strings.TrimSpace(s)
	parts := make([]string, 0, len(s))
	for _, ch := range s {
		if val, ok := mapping[ch]; ok {
			parts = append(parts, val)
		}
	}
	fmt.Print(strings.Join(parts, "+"))
}
